sqlMap.select:
SELECT SERIAL_NUM,POST_NUM,TITLE,WRITER,REPORTING,VIEWS FROM ((SELECT ROW_NUMBER() OVER(ORDER BY POST_NUM DESC)AS RNUM,SERIAL_NUM,POST_NUM,TITLE,WRITER,TO_CHAR(REPORTING, 'YYYY-MM-DD') AS REPORTING,VIEWS FROM POST WHERE 1=1 AND SEG_CODE = #gbn#
AND TITLE LIKE '%' || #searchText# || '%'
)A) WHERE A.RNUM BETWEEN #start# AND #end#
